public class AddTrening : ICommandUR {

	public Trening trening;

	public AddTrening(){

	}

	~AddTrening(){

	}

	/// 
	/// <param name="trening"></param>
	public AddTrening(Trening trening){
		this.trening = trening;
	}

	public string ExecuteA(int id, IDBService service)
	{
		trening.ID = 0;
		return service.AddTreningAdmin(id, trening);
	}

	public string UnExecuteA(int id, IDBService service)
	{
		var osoba = new Admin();
		osoba.ID = id;
